import React, {Component} from "react";

class Book extends Component {
  state = {
    comment: false
  }

  addComment = () => {
    this.setState({
      comment: true
    })
  }



  render(){
    return (
      <div>
        <h2>{this.props.book.title}</h2>
        <img src={this.props.book.img} alt=""></img>
        <button onClick={() => this.addComment}>Add Comment</button>
        {this.state.comment ? <h2>yay</h2> : null}
        <button onClick={() => console.log("hi")}>See All Comments</button>
      </div>
    );
  }  
};

export default Book;
