import React, {Component} from "react";

class Book extends Component {
  state = {
    comment: false,
    commentContent: ""
  }

  renderComment = () => {
    this.setState({
      comment: !this.state.comment
    })
  }

  changeHandler = (e) => {
    this.setState({
      commentContent: e.target.value 
    })
  }

  addComment = () => {
    console.log(this.props.book.comments)
    // this.props.submitComment(this.props.book, this.state.commentContent)
  }


  commentForm = () => {
    return (
    <div>
      <label>
      Add a comment:
      <input type="text" name="comment" onChange={(e) => this.changeHandler(e)}/>
      </label>
      <br></br>
      <input type="submit" value="Submit" onClick={(e) => this.addComment()}/>
    </div>
    )
  }

  render(){
    return (
      <div>
        <h2>{this.props.book.title}</h2>
        <img src={this.props.book.img} alt=""></img>
        <button onClick={() => this.renderComment()}>Add Comment</button>
        {this.state.comment ? this.commentForm() : null}
        <button onClick={() => console.log("hi")}>See All Comments</button>
      </div>
    );
  }  
};

export default Book;
