import React, {Component} from "react";

class Book extends Component {
  state = {
    comment: false
  }

  addComment = () => {
    this.setState({
      comment: true,
      commentContent: ""
    })
  }

  changeHandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value 
    })
  }

  commentForm = () => {
    return (
    <div>
      <label>
      Add a comment:
      <input type="text" name="comment" onChange={(e) => this.changeHandler(e)}/>
      </label>
      <br></br>
      <input type="submit" value="Submit" onClick={(e) => this.props.addBook(this.state)}/>
    </div>
    )
  }

  render(){
    return (
      <div>
        <h2>{this.props.book.title}</h2>
        <img src={this.props.book.img} alt=""></img>
        <button onClick={() => this.addComment()}>Add Comment</button>
        {this.state.comment ? this.commentForm() : null}
        <button onClick={() => console.log("hi")}>See All Comments</button>
      </div>
    );
  }  
};

export default Book;
