import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import BookList from "./containers/BookList";
import Bookshelf from "./containers/Bookshelf";

class App extends React.Component {
  state = {
    books: [],
  }

  componentDidMount(){
    fetch('http://localhost:3005/books')
    .then(function(response) {
      return response.json();
    })
    .then((myJson) => {
      this.setState({
        books: myJson,
      })
    });  
  }

  componentDidUpdate(){
    fetch('http://localhost:3005/books')
    .then(function(response) {
      return response.json();
    })
    .then((myJson) => {
      this.setState({
        books: myJson,
      })
    });
  }

  addBook = (book) => {
    fetch(`http://localhost:3005/books`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(book)
    }).then(res => res.json())
    .then(res => {
      console.log("turtle", book)
      let newArray = [...this.state.books]
      let index = newArray.findIndex(b => b.id === book.id)
      newArray[index] = res
      this.setState({
        books: newArray
      })
    })   
  }

  submitComment = (book) => {
    console.log(book)

    fetch(`http://localhost:3005/books/${book.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(book)
    }).then(res => res.json())
    .then(res => {
      console.log(res)
      let newArray = [...this.state.books]
      let index = newArray.findIndex(b => b.id === book.id)
      console.log("turtle", newArray[index].comments)
      newArray[index] = res
      this.setState({
      books: newArray
      })
    })   
  }

  render(){
    return (
      <div className="book-container">
        <BookList books={this.state.books} addBook={this.addBook} submitComment={this.submitComment}/>
        <Bookshelf />
      </div>
    );
  }
}

export default App;
