import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import BookList from "./containers/BookList";
import Bookshelf from "./containers/Bookshelf";

class App extends React.Component {
  state = {
    books: [],
    formInfo:{}
  }

  componentDidMount(){
    fetch('http://localhost:3005/books')
    .then(function(response) {
      return response.json();
    })
    .then((myJson) => {
      this.setState({
        books: myJson,
        formInfo: {}
      })
    });  
  }

  addBook(book){
    book.preventDefault()
    console.log(book)
  }

  addBook = (book) => {
    fetch(`http://http://localhost:3005/books`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(book)
    }).then(res => res.json())
    .then(res => {
      console.log(book)
      // let newArray = [...this.state.pizzas]
      // let index = newArray.findIndex(p => p.id === pizza.id)
      // newArray[index] = res
      // this.setState({
      // pizzas: newArray
      })   
  }

  render(){
    return (
      <div className="book-container">
        <BookList books={this.state.books} addBook={this.addBook}/>
        <Bookshelf />
      </div>
    );
  }
}

export default App;
